bowtie2.string_script:
def split_cpus = Math.floor(task.cpus/2)
def size = params.large_ref ? '-c' : ''
def fasta = "${index}/${fasta_base}"
def trim5 = params.bt2_trim5 != 0 ? "--trim5 ${params.bt2_trim5}" : ""
def trim3 = params.bt2_trim3 != 0 ? "--trim3 ${params.bt2_trim3}" : ""
def bt2n = params.bt2n != 0 ? "-N ${params.bt2n}" : ""
def bt2l = params.bt2l != 0 ? "-L ${params.bt2l}" : ""

if ( "${params.bt2_alignmode}" == "end-to-end"  ) {
  switch ( "${params.bt2_sensitivity}" ) {
    case "no-preset":
    sensitivity = ""; break
    case "very-fast":
    sensitivity = "--very-fast"; break
    case "fast":
    sensitivity = "--fast"; break
    case "sensitive":
    sensitivity = "--sensitive"; break
    case "very-sensitive":
    sensitivity = "--very-sensitive"; break
    default:
    sensitivity = ""; break
    }
  } else if ("${params.bt2_alignmode}" == "local") {
  switch ( "${params.bt2_sensitivity}" ) {
    case "no-preset":
    sensitivity = ""; break
    case "very-fast":
    sensitivity = "--very-fast-local"; break
    case "fast":
    sensitivity = "--fast-local"; break
    case "sensitive":
    sensitivity = "--sensitive-local"; break
    case "very-sensitive":
    sensitivity = "--very-sensitive-local"; break
    default:
    sensitivity = ""; break

    }
  }


if ( seqtype == 'PE' && ( params.skip_collapse || params.skip_adapterremoval ) ){
"""
bowtie2 -x ${fasta} -1 ${r1} -2 ${r2} -p ${split_cpus} ${sensitivity} ${bt2n} ${bt2l} ${trim5} ${trim3} --maxins ${params.bt2_maxins} --rg-id ILLUMINA-${libraryid} --rg SM:${samplename} --rg PL:illumina --rg PU:ILLUMINA-${libraryid}-${seqtype} 2> "${libraryid}"_bt2.log | samtools sort -@ ${split_cpus} -O bam > "${libraryid}"_"${seqtype}".mapped.bam
samtools index "${libraryid}"_"${seqtype}".mapped.bam ${size}
"""
} else {

"""
bowtie2 -x ${fasta} -U ${r1} -p ${split_cpus} ${sensitivity} ${bt2n} ${bt2l} ${trim5} ${trim3} --rg-id ILLUMINA-${libraryid} --rg SM:${samplename} --rg PL:illumina --rg PU:ILLUMINA-${libraryid}-${seqtype} 2> "${libraryid}"_bt2.log | samtools sort -@ ${split_cpus} -O bam > "${libraryid}"_"${seqtype}".mapped.bam
samtools index "${libraryid}"_"${seqtype}".mapped.bam ${size}
"""
}

