GET_SOFTWARE_VERSIONS.string_process:
process GET_SOFTWARE_VERSIONS {
    publishDir "${params.outdir}",
        mode: params.publish_dir_mode,
        saveAs: { filename -> saveFiles(filename:filename, options:params.options, publish_dir:'pipeline_info', meta:[:], publish_by_meta:[]) }

    conda (params.enable_conda ? "conda-forge::python=3.8.3" : null)
    if (workflow.containerEngine == 'singularity' && !params.singularity_pull_docker_container) {
        container "https://depot.galaxyproject.org/singularity/python:3.8.3"
    } else {
        container "quay.io/biocontainers/python:3.8.3"
    }

    cache false

    input:
    path versions

    output:
    path "software_versions.tsv"     , emit: tsv
    path 'software_versions_mqc.yaml', emit: yaml

    script:                                                                        
    """
    echo $workflow.manifest.version > pipeline.version.txt
    echo $workflow.nextflow.version > nextflow.version.txt
    scrape_software_versions.py &> software_versions_mqc.yaml
    """
}

SAMPLESHEET_CHECK.string_process:
process SAMPLESHEET_CHECK {
    tag "$samplesheet"
    publishDir "${params.outdir}",
        mode: params.publish_dir_mode,
        saveAs: { filename -> saveFiles(filename:filename, options:params.options, publish_dir:'pipeline_info', meta:[:], publish_by_meta:[]) }

    conda (params.enable_conda ? "conda-forge::python=3.8.3" : null)
    if (workflow.containerEngine == 'singularity' && !params.singularity_pull_docker_container) {
        container "https://depot.galaxyproject.org/singularity/python:3.8.3"
    } else {
        container "quay.io/biocontainers/python:3.8.3"
    }

    input:
    path samplesheet

    output:
    path '*.csv'

    script:                                                                    
    """
    check_samplesheet.py \\
        $samplesheet \\
        samplesheet.valid.csv
    """
}

DecomposeAndNormalise.string_process:
process DecomposeAndNormalise {
    tag "${meta.id}"

    input:
    tuple val(meta), path(vcf)
}

SKEWER_TRIMMING.string_script:
"""
    cat > adapters.tsv << END
    >Adapter1
    ${meta["adapter1"]}
    >Adapter2
    ${meta["adapter2"]}
    END
    skewer -x adapters.tsv --threads ${task.cpus} --min 25 --end-quality 25 --format sanger --compress --output trim ${reads.join(' ')}
    skewer --version | awk '/version/ {print("    skewer:", \$NF)}' > version.yml
    """
