gunzip_dbsnp.string_script:
 if ( "${dbsnp_gz}".endsWith(".gz") ) {
  """
  gunzip -d --force $dbsnp_gz
  gunzip -d --force $dbsnp_idx_gz
  """
} else {
  """
  cp $dbsnp_gz dbsnp.vcf
  cp $dbsnp_idx_gz dbsnp.vcf.idx
  """
}

