divideTreeIntoEdges.string_process:
process divideTreeIntoEdges {

	input:
	file divtree from div1tree1

	output:
	file "edge*" into staredges mode flatten

	shell:
	'''
	gotree compute edgetrees -i !{divtree} -o edge -t !{task.cpus}
	'''
}

analyzeStarEdges.string_process:
process analyzeStarEdges {
	tag {staredge.baseName}

	input:
	file staredge from staredges
	file truetree from truetree1.first()

	output:
	file "quartet" into quartets

	shell:
	template 'quartet.sh'
}

transferDistTrue.string_script:
'''
#!/bin/bash
booster -i !{divtree} -b !{truetree} -n empirical -o div_1_transfer_true.nw -@ !{task.cpus}
gotree stats edges -i div_1_transfer_true.nw > transfer_to_true.txt
'''

simulateFasta.string_process:
process simulateFasta{

	input :
	file(tree) from firsttree

	output:
	file("original.fa.gz") into alignOriginalFasta

	shell:
	if (simulator == "seqgen")
		template 'seqgen.sh'
	else if (simulator == "indelible")
		template 'indelible.sh'
	else
		error "Invalid simulation mode: ${simulator} (must be indelible or seqgen)"
}

