divideBootAlign.string_process:
process divideBootAlign{
	tag "${bootFile} : div ${div} - seed ${seed}"

	input:
	set val(div), val(seed), val(nbtaxa), file(bootFile) from dividedBootAlignment

	output:
	set val(div), val(seed), file("boot*.fa.gz") into dividedBootAlignToTree mode flatten

	shell:
	'''
	#!/usr/bin/env bash
	tar -xzvf !{bootFile}
	gzip boot*.fa
	'''
}

divideboots.string_process:
process divideboots{
	tag "${bootFile}:${div}_${seed}"

	input:
	set val(div), val(seed), val(type), file(bootFile) from bootaligns.mix(bootsimualigns)

	output:
	set val(div), val(seed), val(type), file("boot*.fa.gz") into divbootaligns mode flatten

	shell:
	'''
	#!/usr/bin/env bash
	tar -xf !{bootFile}
	'''
}
