fastqc.string_process:
process fastqc{

    input:
	    tuple val(sample), path(r1), path(r2)

    output:
        tuple val(sample), path ("*1_fastqc.zip"), path ("*2_fastqc.zip"), emit: zip

    script:

    def R1 = r1.getName()
    def R2 = r2.getName()

    """
    [ ! -f ${R1} ] && ln -s ${r1} ${R1}
    [ ! -f ${R2} ] && ln -s ${r2} ${R2}
    fastqc --threads ${task.cpus} ${R1} ${R2}
    """
}

