cram_to_fastq.string_process:
process cram_to_fastq {
    tag { "${params.project_name}.${sample_id}.ctF" }
    echo true
    publishDir "${params.out_dir}/${sample_id}", mode: 'copy', overwrite: false
    input:
    set val(sample_id), file(bam_file) from collate

    output:
    set val(sample_id), file("${sample_id}_R1.fastq.gz"), file("${sample_id}_R2.fastq.gz") into fastq

    script:
    """
    ${params.samtools_base}/samtools fastq -1 ${sample_id}_R1.fastq.gz -2 ${sample_id}_R2.fastq.gz -0 /dev/null -s /dev/null -N -F 0x900 --reference ${params.ref_seq} ${bam_file}
    """
}

print_sample_info.string_process:
process print_sample_info {
    tag { "${sample_id}" }
    echo true
    input:
    set val(sample_id), val(gender), file(bam_file) from samples_1
    script:
    """
    printf "[sample_info] sample: ${sample_id}\tGender: ${gender}\tBAM: ${bam_file}\n"
    """
}

run_samtools_stats.string_process:
process run_samtools_stats {
    tag { "${params.project_name}.${sample_id}.rSS" }
    memory { 4.GB * task.attempt } 
    cpus { "${params.bwa_threads}" }
    publishDir "${params.out_dir}/${sample_id}", mode: 'copy', overwrite: false

    input:
    set val(sample_id), file(bam_file) from recal_bam

    output:
    set val(sample_id), file("${sample_id}.md.recal.stats")  into recal_stats

    """
    ${params.samtools_base}/samtools stats  \
    --threads ${params.bwa_threads} \
    ${bam_file} > ${sample_id}.md.recal.stats  \
    """
}

index_bam.string_process:
process index_bam {
    tag { "${sample_id}" }
    echo true
    publishDir "${params.out_dir}/${sample_id}", mode: 'copy', overwrite: false
    input:
    set val(sample_id), file(bam_file) from samples
    output:
    file("*.bai")
    script:
    """
    ${params.samtools_base}/samtools index ${bam_file}
    """
}
