BBMAP_BBSPLIT.string_script:
def args = task.ext.args ?: ''
def prefix = task.ext.prefix ?: "${meta.id}"

def avail_mem = 3
if (!task.memory) {
    log.info '[BBSplit] Available memory not known - defaulting to 3GB. Specify process memory requirements to change this.'
} else {
    avail_mem = task.memory.giga
}

def other_refs = []
other_ref_names.eachWithIndex { name, index ->
    other_refs << "ref_${name}=${other_ref_paths[index]}"
}
if (only_build_index) {
    if (primary_ref && other_ref_names && other_ref_paths) {
        """
        bbsplit.sh \\
            -Xmx${avail_mem}g \\
            ref_primary=$primary_ref \\
            ${other_refs.join(' ')} \\
            path=bbsplit \\
            threads=$task.cpus \\
            $args

        cat <<-END_VERSIONS > versions.yml
        "${task.process}":
            bbmap: \$(bbversion.sh 2>&1)
        END_VERSIONS
        """
    } else {
        log.error 'ERROR: Please specify as input a primary fasta file along with names and paths to non-primary fasta files.'
    }
} else {
    def index_files = ''
    if (index) {
        index_files = "path=$index"
    } else if (primary_ref && other_ref_names && other_ref_paths) {
        index_files = "ref_primary=${primary_ref} ${other_refs.join(' ')}"
    } else {
        log.error 'ERROR: Please either specify a BBSplit index as input or a primary fasta file along with names and paths to non-primary fasta files.'
    }
    def fastq_in  = meta.single_end ? "in=${reads}" : "in=${reads[0]} in2=${reads[1]}"
    def fastq_out = meta.single_end ? "basename=${prefix}_%.fastq.gz" : "basename=${prefix}_%_#.fastq.gz"
    """
    bbsplit.sh \\
        -Xmx${avail_mem}g \\
        $index_files \\
        threads=$task.cpus \\
        $fastq_in \\
        $fastq_out \\
        refstats=${prefix}.stats.txt \\
        $args

    cat <<-END_VERSIONS > versions.yml
    "${task.process}":
        bbmap: \$(bbversion.sh 2>&1)
    END_VERSIONS
    """
}

SALMON_QUANT.string_script:
def args = task.ext.args   ?: ''
prefix   = task.ext.prefix ?: "${meta.id}"

def reference   = "--index $index"
def input_reads = meta.single_end ? "-r $reads" : "-1 ${reads[0]} -2 ${reads[1]}"
if (alignment_mode) {
    reference   = "-t $transcript_fasta"
    input_reads = "-a $reads"
}

def strandedness_opts = [
    'A', 'U', 'SF', 'SR',
    'IS', 'IU' , 'ISF', 'ISR',
    'OS', 'OU' , 'OSF', 'OSR',
    'MS', 'MU' , 'MSF', 'MSR'
]
def strandedness =  'A'
if (lib_type) {
    if (strandedness_opts.contains(lib_type)) {
        strandedness = lib_type
    } else {
        log.info "[Salmon Quant] Invalid library type specified '--libType=${lib_type}', defaulting to auto-detection with '--libType=A'."
    }
} else {
    strandedness = meta.single_end ? 'U' : 'IU'
    if (meta.strandedness == 'forward') {
        strandedness = meta.single_end ? 'SF' : 'ISF'
    } else if (meta.strandedness == 'reverse') {
        strandedness = meta.single_end ? 'SR' : 'ISR'
    }
}
"""
salmon quant \\
    --geneMap $gtf \\
    --threads $task.cpus \\
    --libType=$strandedness \\
    $reference \\
    $input_reads \\
    $args \\
    -o $prefix

cat <<-END_VERSIONS > versions.yml
"${task.process}":
    salmon: \$(echo \$(salmon --version) | sed -e "s/salmon //g")
END_VERSIONS
"""

CAT_FASTQ.string_script:
def args = task.ext.args ?: ''
def prefix = task.ext.prefix ?: "${meta.id}"
def readList = reads.collect{ it.toString() }
if (meta.single_end) {
    if (readList.size > 1) {
        """
        cat ${readList.join(' ')} > ${prefix}.merged.fastq.gz

        cat <<-END_VERSIONS > versions.yml
        "${task.process}":
            cat: \$(echo \$(cat --version 2>&1) | sed 's/^.*coreutils) //; s/ .*\$//')
        END_VERSIONS
        """
    }
} else {
    if (readList.size > 2) {
        def read1 = []
        def read2 = []
        readList.eachWithIndex{ v, ix -> ( ix & 1 ? read2 : read1 ) << v }
        """
        cat ${read1.join(' ')} > ${prefix}_1.merged.fastq.gz
        cat ${read2.join(' ')} > ${prefix}_2.merged.fastq.gz

        cat <<-END_VERSIONS > versions.yml
        "${task.process}":
            cat: \$(echo \$(cat --version 2>&1) | sed 's/^.*coreutils) //; s/ .*\$//')
        END_VERSIONS
        """
    }
}
