orthoFinder.string_process:
process orthoFinder {
    publishDir "results"

    clusterOptions = { "-l select=1:ncpus=12:mem=5gb,walltime=48:00:00" }
    module 'singularity'

    script:
    """
    singularity run -B /scratch2 ~/singularity_containers/orthofinder.simg -f ${params.directory} -t 12 -a 1 \
-S diamond -M msa -A mafft -T fasttree -o ${baseDir}/results/
    """
}

indexChr.string_process:
process indexChr {
    publishDir "$baseDir/data/references"

    input:
        set dataset_id, file(chromosome) from index_chromosomes

    output:
        file "${dataset_id}.???" into indices

    clusterOptions = { "-l select=1:ncpus=2:mem=12gb,walltime=05:00:00" }
    module 'singularity'

    script:
    """
    singularity run -B /scratch2,/zfs ~/singularity_containers/last.simg lastdb -c -P 2 ${dataset_id} ${chromosome}
    """
}
