fastqc.string_process:
process fastqc {
    label "low_cores"
    label "min_mem"
    label "regular_queue"

    tag "${meta.sample_id}"

    publishDir "${params.outdir}/${opts.publish_dir}",
        mode: "copy", 
        overwrite: true,
        saveAs: { filename ->
                      if (opts.publish_results == "none") null
                      else filename }

    container 'quay.io/biocontainers/fastqc:0.11.9--0'

    input:
        val opts
        tuple val(meta), path(reads)

    output:
        path "*.zip", emit: report

    script:
    args = ""
        if(opts.args && opts.args != '') {
            ext_args = opts.args
            args += ext_args.trim()
        }

    prefix = opts.suffix ? "${meta.sample_id}${opts.suffix}" : "${meta.sample_id}"

    fastqc_command = "fastqc ${args} --threads ${task.cpus} $reads"
    if (params.verbose){
        println ("[MODULE] fastqc command: " + fastqc_command)
    }


    readList = reads.collect{it.toString()}
    if(readList.size > 1){
            """
            ${fastqc_command}
            mv ${reads[0].simpleName}_fastqc.zip ${prefix}_r1_fastqc.zip
            mv ${reads[1].simpleName}_fastqc.zip ${prefix}_r2_fastqc.zip
            """
    }
    else {
            """
            ${fastqc_command}
            mv ${reads[0].simpleName}_fastqc.zip ${prefix}_fastqc.zip
            """
    }
}

