        } else {
            ultraplex_command = "ultraplex --inputfastq ${readList[0]} --barcodes $barcode_file --threads ${task.cpus} ${args}"
        }

        if (params.verbose){
            println ("[MODULE] ultraplex command: " + ultraplex_command)
        }

        """
        ${ultraplex_command}
        """
}

