CAGEscanFindMolecules.string_script:
command = """
    cagescan-find-molecules.sh -d 1 -g 0.5 -n ${params.count} -p 20 ${reads1} > ${sampleName}.molecules.txt
    """

if (params.verbose){
    println ("[MODULE] CAGEscan find-molecules command: " + command)
}

"""
${command}
"""

CAGEscanAssemble.string_script:
command = """
    cagescan-assemble.sh ${reads1} ${reads2} ${molecules} > ${sampleName}.assembled.fq
    """

if (params.verbose){
    println ("[MODULE] CAGEscan assemble command: " + command)
}

"""
${command}
"""

CAGEscanMap.string_script:
command = """
    cagescan-map.sh ${index}/index ${assembled} > ${sampleName}.maf
    """

if (params.verbose){
    println ("[MODULE] CAGEscan map command: " + command)
}

"""
${command}
"""

CAGEscanCountHits.string_script:
command = """
    cagescan-count-hits.sh ${mapped} |
       awk '{print \$0 "\t${sampleName}"}' > "${sampleName}".hits.txt
    """

if (params.verbose){
    println ("[MODULE] CAGEscan count hits command: " + command)
}

"""
${command}
"""

CAGEscanBuildTranscripts.string_script:
command = """
    cagescan-build-transcripts.py -b20 -m0.001 ${mapped} > ${sampleName}.transcripts.txt
    """

if (params.verbose){
    println ("[MODULE] CAGEscan build transcripts command: " + command)
}

"""
${command}
"""

CAGEscanConvertToBED12.string_script:
command = """
    cagescan-to-bed.py -s1e6 ${transcripts} |
       sort -k1,1 -k2,2n -k3,3n -k4,4 -k6,6 > ${sampleName}.bed
    """

if (params.verbose){
    println ("[MODULE] CAGEscan convert to BED12 command: " + command)
}

"""
${command}
"""

hello2.string_process:
process hello2 {
	input:
		path file1
	output:
		tuple val("blah"), path(file1, includeInputs:true)

	script:
	"""
	echo ${file1}
	pwd
	ls -lahrt ${file1}
	"""
}

bbmap_version.string_process:
process bbmap_version {
    container = 'cagescan/bbmap:38.86'
    input:
    output:
        stdout()
    script:
    command = ''
    if (params.verbose){
        command = "bbmap.sh -version 2>&1 | grep 'BBMap version'"
    }
    """
    ${command}
    """
}

listFilesFromInput.string_process:
process listFilesFromInput {
	input:
		path(file1)
		path(file2)
	output:
		stdout
	script:
	"""
	echo "Next pair..."
	ls -l ${file1}
	ls -l ${file2}
	"""
}
