GATK4_MUTECT2.string_script:
def args = task.ext.args ?: ''
def prefix = task.ext.prefix ?: "${meta.id}"
def panels_command = ''
def normals_command = ''
def interval_command = ''

def inputs_command = '-I ' + input.join( ' -I ')

if(run_pon) {
    panels_command = ''
    normals_command = ''

} else if(run_single) {
    panels_command = " --germline-resource $germline_resource --panel-of-normals $panel_of_normals"
    normals_command = ''

} else if(run_mito){
    panels_command = "-L ${interval_label} --mitochondria-mode"
    normals_command = ''

} else {
    panels_command = " --germline-resource $germline_resource --panel-of-normals $panel_of_normals --f1r2-tar-gz ${prefix}.f1r2.tar.gz"
    normals_command = '-normal ' + which_norm.join( ' -normal ')
}

if(interval_list) {
    interval_command = '-L ' + interval_list.join( ' -L ')
}

def avail_mem = 3
if (!task.memory) {
    log.info '[GATK Mutect2] Available memory not known - defaulting to 3GB. Specify process memory requirements to change this.'
} else {
    avail_mem = task.memory.giga
}
"""
gatk --java-options "-Xmx${avail_mem}g" Mutect2 \\
    -R ${fasta} \\
    ${inputs_command} \\
    ${normals_command} \\
    ${panels_command} \\
    ${interval_command} \\
    -O ${prefix}.vcf.gz \\
    -bamout ${prefix}.bam \\
    $args

cat <<-END_VERSIONS > versions.yml
"${task.process}":
    gatk4: \$(echo \$(gatk --version 2>&1) | sed 's/^.*(GATK) v//; s/ .*\$//')
END_VERSIONS
"""

SALMON_QUANT.string_script:
def args = task.ext.args   ?: ''
prefix   = task.ext.prefix ?: "${meta.id}"

def reference   = "--index $index"
def input_reads = meta.single_end ? "-r $reads" : "-1 ${reads[0]} -2 ${reads[1]}"
if (alignment_mode) {
    reference   = "-t $transcript_fasta"
    input_reads = "-a $reads"
}

def strandedness_opts = [
    'A', 'U', 'SF', 'SR',
    'IS', 'IU' , 'ISF', 'ISR',
    'OS', 'OU' , 'OSF', 'OSR',
    'MS', 'MU' , 'MSF', 'MSR'
]
def strandedness =  'A'
if (lib_type) {
    if (strandedness_opts.contains(lib_type)) {
        strandedness = lib_type
    } else {
        log.info "[Salmon Quant] Invalid library type specified '--libType=${lib_type}', defaulting to auto-detection with '--libType=A'."
    }
} else {
    strandedness = meta.single_end ? 'U' : 'IU'
    if (meta.strandedness == 'forward') {
        strandedness = meta.single_end ? 'SF' : 'ISF'
    } else if (meta.strandedness == 'reverse') {
        strandedness = meta.single_end ? 'SR' : 'ISR'
    }
}
"""
salmon quant \\
    --geneMap $gtf \\
    --threads $task.cpus \\
    --libType=$strandedness \\
    $reference \\
    $input_reads \\
    $args \\
    -o $prefix

cat <<-END_VERSIONS > versions.yml
"${task.process}":
    salmon: \$(echo \$(salmon --version) | sed -e "s/salmon //g")
END_VERSIONS
"""
